    }
    await db.collection("sub-challenges").doc().set({
      uid: currentUser.uid,
      images: fileUrl,
    });
  };

  return (
    <div>
      <h2>Create Challenge</h2>
      <div>
        <button type="button" onClick={handleOpen}>
          Add a Sub Challenge
        </button>
        <AddSubChallengeModal open={open} handleClose={handleClose} />
      </div>
    </div>
  );
}
export default CreateChallenge;
